package aoc22;

import static utils.Helper.convertCharArrayToSet;

/**
 * <a href="https://adventofcode.com/2022/day/6">Advent of Code 2022 Day 6</a>
 */
public class Day06Part1 {

	public static void main(String[] args) throws Exception {
		String input =
				"";

		int subLen = 4;
		StringBuilder answer = new StringBuilder();
		for (int i = 0; i < input.length() - subLen; i++) {
			if (convertCharArrayToSet(input.substring(i, i + subLen).toCharArray()).size() == subLen) {
				answer.append(i + subLen);
				break;
			}
		}

		System.out.println(answer);

		String expected = "1876";
		if (!answer.toString().equals(expected)) {
			throw new RuntimeException(String.format("Answer %s doesn't match expected %s", answer, expected));
		}
	}
}