package aoc22;

import static utils.Helper.convertCharArrayToSet;

/**
 * <a href="https://adventofcode.com/2022/day/6">Advent of Code 2022 Day 6</a>
 */
public class Day6Part1 {

	public static void main(String[] args) throws Exception {
		String input =
				"";

		int subLen = 4;
		for (int i = 0; i < input.length() - subLen; i++) {
			if (convertCharArrayToSet(input.substring(i, i + subLen).toCharArray()).size() == subLen) {
				System.out.print(i + subLen);
				break;
			}
		}
	}
}