package aoc22;

import java.util.HashSet;

public class Day6Part1 {

	public static void main(String[] args) throws Exception {
		String input = "";
		char[] ary = input.toCharArray();
		HashSet<Character> charSet;
		int ctr = 4;

		for (int i = 3; i < ary.length; i++) {
			charSet = new HashSet<>();
			for (int j = 0; j < 4; j++) {
				charSet.add(ary[i - j]);
			}

			if (charSet.size() == 4){
				System.out.print(ctr);
				break;
			}
			ctr++;
		}
	}
}