package aoc22;

import java.util.HashSet;

public class Day6Part2 {

	public static void main(String[] args) throws Exception {
		String input =
				"";
		char[] ary = input.toCharArray();
		HashSet<Character> charSet;
		int ctr = 14;

		for (int i = 13; i < ary.length; i++) {
			charSet = new HashSet<>();
			for (int j = 0; j < 14; j++) {
				charSet.add(ary[i - j]);
			}

			if (charSet.size() == 14){
				System.out.print(ctr);
				break;
			}
			ctr++;
		}
	}
}
